import React, {useCallback, useEffect, useMemo, useRef, useState} from 'react';
import CountriesList from "../companents/CountriesList";
import Navbar from "../companents/navbar";
import styled from "styled-components";
import Modal from "../companents/modal";
import Text from "../companents/Text";
import TextInput from "../companents/text-input";
import Button from "../companents/Button";
import {createBook, deleteBook, getBooks} from "../api/entities";
import md5 from "md5";

const Home = () => {
    const [addModal,setAddModal] = useState(false);
    const [isbn,setIsbn] = useState('')
    const [loader,setLoader] = useState(false)
    const [bookList,setBookList] = useState([])
    const [editStatus,setEditStatus] = useState(null);
    const isbnRef = useRef(null);
    const statusRef = useRef(null);

    useEffect(() => {
        getBookList();
    },[])

    const createBtn = () => {
        if(isbn){
            setLoader(true);
            createBook({isbn})
                .then(res => {
                    getBookList();
                    setAddModal(false);
                })
                .catch(err => console.warn(err))
                .finally(()=> setLoader(false));
        }
    }

    const getBookList = () =>{
        getBooks().then(res => {
          setBookList(res.data.data)
        }).catch(err => console.warn(err))
    }

    const deleteBookHandle = useCallback((id) => {
        deleteBook(id).then(res =>{
            getBookList()
        }).catch(err => console.warn(err))
    },[])

    const list = useMemo(()=>{
        return bookList;
    },[bookList])

    return (
        <>
            <Navbar/>
            <hr/>
            <CountriesList
                deleteBookHandle = {deleteBookHandle}
                onEditBtn={(item)=>setEditStatus(item)}
                list={list}
            />
            <PlusIcon onClick={()=>setAddModal(true)}><span>+</span></PlusIcon>
            <Modal show={addModal} onClose={()=>setAddModal(false)}>
                <ModalContainer>
                    <Text size={24} color={'#000'} weight={'600'}>Create book</Text>
                    <TextInput
                        label={'ISBN'}
                        placeholder={'ISBN'}
                        value={isbn}
                        onChange={ev => setIsbn(ev.target.value)}
                    />
                    <Button onClick={createBtn}>{loader ? 'Loading...':'Create'}</Button>
                </ModalContainer>
            </Modal>
            <Modal show={editStatus} onClose={()=>setEditStatus(null)}>
                <ModalContainer>
                    <Text size={24} color={'#000'} weight={'600'}>{editStatus?.book?.title}</Text>
                    <TextInput
                        label={'ISBN'}
                        placeholder={'ISBN'}
                        value={editStatus}
                        onChange={ev => setIsbn(ev.target.value)}
                    />
                    <Button onClick={createBtn}>{loader ? 'Loading...':'Create'}</Button>
                </ModalContainer>
            </Modal>
        </>
    );
};

const PlusIcon = styled.div`
  width: 60px;
  height: 60px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: ${prop => prop.theme.colors.main};
  position: fixed;
  bottom: 25px;
  right: 30px;
  cursor: pointer;
  transition: 0.2s ease-in;
  &:hover{
    span{
      transform: rotate(90deg);
    }
    &:after{
      content: 'Create book';
      color: #fef7e0;
      font-size: 18px;
      font-weight: 660;
      margin-left: 12px;
      transition: 0.4s ease-in;
    }
    width: 170px;
    border-radius: 30px;
  }

  span{
    //margin-top: -5px;
    height: 40px;
    font-size: 30px;
    color: #fef7e0;
    font-weight: bold;
    transition: 0.4s ease-in;

  }
`;

const ModalContainer = styled.div`
  width: 400px;
  display: flex;
  flex-direction: column;
  gap: 20px;
`;
export default Home;
