import React from 'react';
import CountriesList from "../companents/CountriesList";
import Navbar from "../companents/navbar";
import styled from "styled-components";

const Home = () => {
    return (
        <>
            <Navbar/>
            <hr/>
            <CountriesList/>
            <PlusIcon><span>+</span></PlusIcon>
        </>
    );
};

const PlusIcon = styled.div`
  width: 55px;
  height: 55px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: ${prop => prop.theme.colors.main};
  position: fixed;
  bottom: 15px;
  right: 30px;
  cursor: pointer;
  transition: 0.2s ease-in;
  &:hover{
    //width: 200px;
    p{
      transform: rotate(90deg);
    }
    &:after{
      content: 'Create book';
      color: #fef7e0;
      font-size: 18px;
      font-weight: 660;
      margin-left: 12px;

    }
    width: 170px;
    border-radius: 30px;
  }

  span{
    margin-top: -5px;
    font-size: 30px;
    color: #fef7e0;
    font-weight: bold;
    transition: 0.2s ease-in;

  }
`;
export default Home;
