import React, {useState} from 'react';
import Container from "../Container";
import {HomeLink, Wrapper} from "./style";
import Text from "../Text";
import HomeIcon from '../../assets/shelf.png'
import useScreenSize from "../../hooks/useScreenSize";
import Button from "../Button";
import {useDispatch, useSelector} from "react-redux";
import {logOute} from "../../store/loginSlice";

const Navbar = () => {
    const {width} = useScreenSize()
    const dispatch = useDispatch();
    const user = useSelector(store=>store.auth.user);

    return (
        <Container shadow={true}>
            <Wrapper>
                <div className={'justify-center'}>
                    <HomeLink to={"/"}>
                        <img src={HomeIcon} alt={'home-icon'}/>
                        <Text size={width > 480 ? 24 : 18} color={'#000'} weight={'600'}>Books shelf</Text>
                    </HomeLink>
                </div>
                {user && <Button onClick={()=>dispatch(logOute())}>Log Oute</Button>}

            </Wrapper>
        </Container>
    );
};

export default Navbar;
