import React, {useState} from 'react';
import CountriesList from "../companents/CountriesList";
import Navbar from "../companents/navbar";
import styled from "styled-components";
import Modal from "../companents/modal";

const Home = () => {
    const [addModal,setAddModal] = useState(false);

    return (
        <>
            <Navbar/>
            <hr/>
            <CountriesList/>
            <PlusIcon onClick={()=>setAddModal(true)}><span>+</span></PlusIcon>
            <Modal show={addModal} onClose={()=>setAddModal(false)}>

            </Modal>
        </>
    );
};

const PlusIcon = styled.div`
  width: 60px;
  height: 60px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: ${prop => prop.theme.colors.main};
  position: fixed;
  bottom: 25px;
  right: 30px;
  cursor: pointer;
  transition: 0.2s ease-in;
  &:hover{
    span{
      transform: rotate(90deg);
    }
    &:after{
      content: 'Create book';
      color: #fef7e0;
      font-size: 18px;
      font-weight: 660;
      margin-left: 12px;
      transition: 0.4s ease-in;
    }
    width: 170px;
    border-radius: 30px;
  }

  span{
    //margin-top: -5px;
    height: 40px;
    font-size: 30px;
    color: #fef7e0;
    font-weight: bold;
    transition: 0.4s ease-in;

  }
`;
export default Home;
