import React from 'react';
import Container from "../Container";
import Text from "../Text";
import Button from "../Button";
import {BtnWrapper, CardItem, CardsWrapper, DescriptionItem, ImageWrapper} from "./style";
import useScreenSize from "../../hooks/useScreenSize";
import BookShelf from '../../assets/shelf.png'
const CountriesList = ({list,deleteBookHandle,onEditBtn}) => {
    const {width} = useScreenSize();

    return (
        <Container>
            <div style={{marginBottom:24}}>
                <Text size={width > 700 ? 32 : 20} color={'#000'} weight={'600'}>
                    Books
                </Text>
            </div>
            <CardsWrapper>
                {(list||[]).map(item=>(
                    <CardItem key={item.id}>
                        <span style={{background:item.status===0?"green":item.status===1?"blue":'red'}}>{item.status===0?"New":item.status===1?"In progress":'Finished'}</span>
                        <ImageWrapper><img  alt={item.capital || BookShelf} src={item?.book?.cover} title={item.book.author||'image'}/></ImageWrapper>
                        <DescriptionItem>
                            <Text>Title:</Text>
                            <Text size={16} color={'#000'} weight={'600'}>
                                {item?.book?.title||'n/a'}
                            </Text>
                        </DescriptionItem>
                        <DescriptionItem>
                            <Text>Author:</Text>
                            <Text size={16} color={'#000'} weight={'600'}>
                                {item?.book?.author ||'n/a'}
                            </Text>
                        </DescriptionItem>
                        <DescriptionItem>
                            <Text>Published:</Text>
                            <Text size={16} color={'#000'} weight={'600'}>
                                {item?.book?.published ||'n/a'}
                            </Text>
                        </DescriptionItem>
                        <DescriptionItem>
                            <Text>Pages:</Text>
                            <Text size={16} color={'#000'} weight={'600'}>
                                {item?.book?.pages ||'n/a'}
                            </Text>
                        </DescriptionItem>
                        <BtnWrapper>
                            <Button style={{padding:'8px 16px'}} onClick={()=>onEditBtn(item)}>Edit status</Button>
                            <Button style={{padding:'8px 16px',background:'red'}} onClick={()=>deleteBookHandle(item?.book?.id)}>Delete book</Button>
                        </BtnWrapper>
                    </CardItem>
                ))}


            </CardsWrapper>

        </Container>
    );
};

export default CountriesList;
