import axios from 'axios';
import { BASE_URL, DEFAULT_HEADERS } from '../constants';
import { store } from '../store';

const httpClient = axios.create({
    withCredentials: true,
    baseURL: BASE_URL,
    DEFAULT_HEADERS,
});

httpClient.interceptors.response.use(
    (response) => {
        console.log('RESPONSE: ', response);
        return response;
    },
    (error) => {
        console.log('error ====>', error);
        // store.dispatch(setAlertAction({ type: 'error', message: error?.message }));
        let status = (error.response && error.response.status) || 0;
        if (status === 401) {
            //clear access token
            console.log('status ====>', error);

            // RootNavigation.navigate(NAVIGATORS.LOGIN);
            return Promise.reject('login or password invalid');
        }
        return Promise.reject(error);
    }
);

httpClient.interceptors.request.use(async (config) => {
    let _access_token;
    // try {
    //     let token = await AsyncStorage.getItem('@token');
    //     // const { access_token } = JSON.parse(token) || null;
    //     _access_token = (await JSON.parse(token)) || null;
    // } catch (error) {
    //     console.log(error);
    //     _access_token = null;
    // }
    console.log('configgggggg-',store.getState().auth.user)
    console.log('configgggggg-',config)
    if (_access_token) {
        config.headers = Object.assign(config.headers, {
            Authorization: 'Bearer ' + _access_token,
        });
        return config;
    } else {
        return config;
    }
});

export const httpGet = (url, params) => httpClient(url, { method: 'get', ...params });

export const httpPost = (url, params) => httpClient(url, { method: 'post', ...params });

export const httpPut = (url, params) => httpClient(url, { method: 'put', ...params });

export const httpPatch = (url, params) => httpClient(url, { method: 'patch', ...params });

export const httpDelete = (url, params) => httpClient(url, { method: 'delete', ...params });
