import React from 'react';
import styled from "styled-components";

const Container = ({children,shadow,...props}) => {

    return (
        <Wrapper shadow={true} {...props}>
            {children}
        </Wrapper>
    );
};


const Wrapper = styled.section`
  width:1080px;
  margin: auto;
  box-shadow: ${props=> props.shadow?'3px 3px 4px #eee':null};
  @media (max-width: 1080px) {
    width: calc(100% - 24px);
  }
  padding: 0px 12px;
`

export default Container;
