    if (pm.request.url.getPath()=="/signup"){
        let body = JSON.parse(pm.request.body)
        pm.collectionVariables.set("key", body.key);
        pm.collectionVariables.set("secret", body.secret);
        console.log("updated key and secret: "+body.key+' '+body.secret)
    }else{
        let signstr = pm.request.method+
            pm.request.url.getPathWithQuery()+
            pm.request.body+
            pm.collectionVariables.get("secret")
        let sign = signature = CryptoJS.MD5(signstr).toString();
        console.log("signstr: "+signstr)
        console.log("sign: "+sign)
        pm.collectionVariables.set("sign",sign)
    }
    if (user) {
        const sign = config.method+config.baseURL+config.url+(config.data?JSON.stringify(config.data):'')+user.secret
        console.log('signnnnn',sign)
        config.headers = Object.assign(config.headers, {
           'Content-Type': 'application/json',
            Key: user.key,
            Sign: md5(sign).toString(),
        });
        return config;
    } else {
        config.headers = Object.assign(config.headers, {
            'Content-Type': 'application/json',
        })
        return config;
    }
});

export const httpGet = (url, params) => httpClient(url, { method: 'get', ...params });

export const httpPost = (url, params) => httpClient(url, { method: 'post', ...params });

export const httpPut = (url, params) => httpClient(url, { method: 'put', ...params });

export const httpPatch = (url, params) => httpClient(url, { method: 'patch', ...params });

export const httpDelete = (url, params) => httpClient(url, { method: 'delete', ...params });
