import React, {useState} from 'react';
import CountriesList from "../companents/CountriesList";
import Navbar from "../companents/navbar";
import styled from "styled-components";
import Modal from "../companents/modal";
import Text from "../companents/Text";
import TextInput from "../companents/text-input";

const Home = () => {
    const [addModal,setAddModal] = useState(false);

    return (
        <>
            <Navbar/>
            <hr/>
            <CountriesList/>
            <PlusIcon onClick={()=>setAddModal(true)}><span>+</span></PlusIcon>
            <Modal show={addModal} onClose={()=>setAddModal(false)}>
                <ModalWrapper>
                    <Text size={24} color={'#000'} weight={'600'}>Add book</Text>
                    <TextInput label={'ISBN'} placeholder={'ISBN'}/>
                </ModalWrapper>

            </Modal>
        </>
    );
};

const PlusIcon = styled.div`
  width: 60px;
  height: 60px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: ${prop => prop.theme.colors.main};
  position: fixed;
  bottom: 25px;
  right: 30px;
  cursor: pointer;
  transition: 0.2s ease-in;
  &:hover{
    span{
      transform: rotate(90deg);
    }
    &:after{
      content: 'Create book';
      color: #fef7e0;
      font-size: 18px;
      font-weight: 660;
      margin-left: 12px;
      transition: 0.4s ease-in;
    }
    width: 170px;
    border-radius: 30px;
  }

  span{
    //margin-top: -5px;
    height: 40px;
    font-size: 30px;
    color: #fef7e0;
    font-weight: bold;
    transition: 0.4s ease-in;

  }
`;

const ModalWrapper = styled.div`
  width: 400px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  input{
    flex: 1;
  }
`;
export default Home;
