import {useTranslation} from "react-i18next";
import Text from "../Text";
import HomeIcon from '../../assets/shelf.png'
import {useLocation} from "react-router-dom";
import useScreenSize from "../../hooks/useScreenSize";
import Button from "../Button";
import {useDispatch} from "react-redux";

const languagesList = [
    {id:1,name: 'UZ', code: 'uz'},
    {id:2,name: 'RU', code: 'ru'},
    {id:3,name: 'EN', code: 'en'},
]

const Navbar = () => {
    const { t,i18n } = useTranslation();
    const location = useLocation()?.pathname;
    const {width} = useScreenSize()
const dispatch = useDispatch();
    return (
        <Container shadow={true}>
            <Wrapper>
                <div className={'justify-center'}>
                    <HomeLink to={"/"}>
                        <img src={HomeIcon} alt={'home-icon'}/>
                        <Text size={width > 480 ? 24 : 18} color={'#000'} weight={'600'}>Books shelf</Text>
                    </HomeLink>
                </div>
               <Button onClick={()=>dispatch(logOute())}>Log Oute</Button>

            </Wrapper>
        </Container>
    );
};

export default Navbar;
