import React, {useState} from 'react';
import Container from "../companents/Container";
import Text from "../companents/Text";
import TextInput from "../companents/text-input";
import styled from "styled-components";
import Navbar from "../companents/navbar";
import Button from "../companents/Button";

const Register = () => {
    const [loader,setLoader] = useState(false);


    const submit = (ev) =>{
        ev.preventDefault();
        const data = {
            name: ev.target[0].value,
            email: ev.target[1].value,

        }
        console.log('ev----',ev.target[0].value)
    }
    return (
        <>
            <Navbar/>
            <hr/>
            <Container>
                <Wrapper>
                    <Title>
                        <Text size={30} color={'#000'} weight={'600'}>Register</Text>
                    </Title>
                    <InputWrapper onSubmit={submit}>
                        <TextInput label={'Name'} iconLeft={'user'} placeholder={'John Doe'}/>
                        <TextInput label={'Email'} iconLeft={'email'} type={'email'} placeholder={'Email@email.com'}/>
                        <TextInput label={'Password'} type={'password'}/>
                        <TextInput label={'Repeat password'} type={'password'}/>
                        <Button type={'submit'}>{loader?'Loading...':'Register'}</Button>
                    </InputWrapper>
                </Wrapper>
            </Container>
        </>

    );
};

const Wrapper = styled.div`
  width: 60%;
  margin: auto;
  background: #f3f3f3;
  padding: 50px 0px;
  border-radius: 10px;
`;
const Title = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
`;
const InputWrapper = styled.form`
  width: 80%;
  margin: auto;
  display: flex;
  flex-direction: column;
  gap: 16px;
`;


export default Register;
