import React from 'react';
import Text from "../Text";
import TextInput from "../text-input";
import Button from "../Button";
import Modal from "../modal";
import styled from "styled-components";

const CreateModal = ({show,onClose,handleBtn,loading,item,ref}) => {
    return (
        <Modal show={show} onClose={()=>onClose()}>
            <ModalContainer>
                <Text size={24} color={'#000'} weight={'600'}>{item ? item?.book?.title:'Create book'}</Text>
                <TextInput
                    label={'ISBN'}
                    placeholder={'ISBN'}
                    ref={ref}
                    // value={isbn}
                    // onChange={ev => setIsbn(ev.target.value)}
                />
                <Button onClick={handleBtn}>{loading ? 'Loading...':'Create'}</Button>
            </ModalContainer>
        </Modal>
    );
};
const ModalContainer = styled.div`
  width: 400px;
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

export default CreateModal;
